import base64
import hashlib
import hmac
import requests
import secrets
import time

# Configuration
API_URL = "http://localhost:8000/api/ping"
KEY_ID = "pwop"  # Replace with your actual HMAC token ID from database
SECRET = ""  # Replace with your actual secret from database


def generate_hmac_signature(key_id, timestamp, nonce, secret):
    """Generate HMAC-SHA256 signature"""
    data = f"{key_id}:{timestamp}:{nonce}"
    signature = hmac.new(
        secret.encode('utf-8'),
        data.encode('utf-8'),
        hashlib.sha256
    ).digest()
    return base64.b64encode(signature).decode('utf-8')


def send_hmac_request(url, key_id, secret):
    """Send authenticated HMAC request"""
    timestamp = str(int(time.time()))
    nonce = secrets.token_hex(16)

    signature = generate_hmac_signature(key_id, timestamp, nonce, secret)

    headers = {
        "X-API-KEY-ID": key_id,
        "X-TIMESTAMP": timestamp,
        "X-NONCE": nonce,
        "X-SIGNATURE": signature
    }

    print(f"Sending request to: {url}")
    print(f"Headers: {headers}")

    response = requests.get(url, headers=headers)

    print(f"\nResponse Status: {response.status_code}")
    print(f"Response Body: {response.text}")

    return response


if __name__ == "__main__":
    try:
        send_hmac_request(API_URL, KEY_ID, SECRET)
    except Exception as e:
        print(f"Error: {e}")
